import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap styles
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import axios from 'axios'; // Import axios for making HTTP requests
import './TasksPage.css'; // Custom styles
import { useAuth } from "../AuthContext";

const TasksPage = () => {
  const navigate = useNavigate(); // To navigate programmatically
  const [projects, setProjects] = useState([]); // State for projects where user is a team member
  const { user } = useAuth(); // Auth context to get the current user
  const [showTaskForm, setShowTaskForm] = useState(false); // Control task form visibility
  const [newTask, setNewTask] = useState({ name: '', description: '', status: 'Ongoing', due_date: '', assignedTo: [] });
  const [teamMembers, setTeamMembers] = useState([]); // Store team members for the assigned field

  // Fetch projects and tasks when the component mounts
  useEffect(() => {
    const fetchProjectsAndTasks = async () => {
      try {
        if (!user) {
          console.log("User is not logged in.");
          return; // Exit if user is not logged in
        }

        // Fetch projects of the user
        const response = await axios.get(`http://localhost:5000/api/user/${user.id}/projects`);
        const fetchedProjects = response.data; // Access the actual project data
        setProjects(fetchedProjects); // Set the projects state
      } catch (error) {
        console.error("Error fetching projects or tasks:", error);
      }
    };

    fetchProjectsAndTasks();
  }, [user]);

  // Handle adding a new task
  const handleAddTask = async (projectId) => {
    try {
      const response = await axios.post(`http:/localhost:5000/api/task/${projectId}`, newTask);
      // After adding the task, refresh the project list to include the new task
      setProjects((prevProjects) => prevProjects.map((project) => 
        project._id === projectId ? { ...project, tasks: [...project.tasks, response.data] } : project
      ));
      setShowTaskForm(false); // Hide the task form after adding the task
      setNewTask({ name: '', description: '', status: 'Ongoing', due_date: '', assignedTo: [] }); // Reset the task form
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  // Function to remove a task
const handleRemoveTask = async (taskId, projectId) => {
  try {
    // Make an API call to delete the task by its ID
    await axios.delete(`http/localhost:5000/api//${projectId}/tasks/${taskId}`);

    // Update the state to remove the task from the project in the UI
    setProjects((prevProjects) => 
      prevProjects.map((project) =>
        project._id === projectId
          ? { ...project, tasks: project.tasks.filter((task) => task._id !== taskId) }
          : project
      )
    );
  } catch (error) {
    console.error("Error removing task:", error);
  }
};


  // Fetch team members for a project
  const fetchTeamMembers = async (projectId) => {
    try {
      const response = await axios.get(`/api/projects/${projectId}/team-members`);
      setTeamMembers(response.data); // Set the list of team members
    } catch (error) {
      console.error("Error fetching team members:", error);
    }
  };

  // Handle the form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  const handleAssignedToChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((option) => option.value);
    setNewTask({ ...newTask, assignedTo: selectedOptions });
  };

  return (
    <div className="container tasks-page mt-4">
      {projects.length > 0 ? (
        projects.map((project) => (
          <div key={project._id} className="card project-detail-card mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h2 className="mb-0">{project.name}
                <span className={`badge ${project.status === 'Ongoing' ? 'bg-warning' : 'bg-success'} ms-2`}>
                  {project.status}
                </span>
              </h2>
              {project.manager === user.id && (
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    setShowTaskForm(true);
                    fetchTeamMembers(project._id); // Fetch team members when opening task form
                  }}
                >
                  Add Task
                </button>
              )}
            </div>

            {/* List of Tasks for the current project */}
            <div className="card-body">
              <div className="row">
                {project.tasks && project.tasks.length > 0 ? (
                  project.tasks.map((task) => (
                    <div key={task._id} className="col-12 mb-3">
                      <div className="card h-100 task-card">
                        <div className="card-body">
                          <h5 className="card-title">{task.name}</h5>
                          <p className="card-text">Description: {task.description}</p>
                          <p className="card-text">
                            <span className={`badge ${task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                              {task.status}
                            </span>
                          </p>
                          <p className="card-text">Due Date: {new Date(task.due_date).toLocaleDateString()}</p>
                          <p className="card-text">Project: {project.name}</p> {/* Display project name */}
                          <div className="d-flex justify-content-between">
                            <button
                              className="btn btn-outline-primary"
                              onClick={() => navigate(`/task-details/${task._id}`)}
                            >
                              Manage
                            </button>
                            <button
                              className="btn btn-danger btn-sm"
                              onClick={() => handleRemoveTask(project._id,task._id)}
                            >
                              Remove
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No tasks found for this project.</p>
                )}
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No projects found for the current user.</p>
      )}

      {/* Add Task Form */}
      {showTaskForm && (
        <div className="card task-form-card mt-4">
          <div className="card-body">
            <h5>Add New Task</h5>
            <form>
              <div className="mb-3">
                <label className="form-label">Task Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  value={newTask.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Description</label>
                <textarea
                  className="form-control"
                  name="description"
                  value={newTask.description}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Status</label>
                <select
                  className="form-select"
                  name="status"
                  value={newTask.status}
                  onChange={handleInputChange}
                  required
                >
                  <option value="Ongoing">Ongoing</option>
                  <option value="Completed">Completed</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Due Date</label>
                <input
                  type="date"
                  className="form-control"
                  name="due_date"
                  value={newTask.due_date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Assigned To</label>
                <select
                  multiple
                  className="form-select"
                  name="assignedTo"
                  value={newTask.assignedTo}
                  onChange={handleAssignedToChange}
                  required
                >
                  {teamMembers.map((member) => (
                    <option key={member.id} value={member.id}>
                      {member.name}
                    </option>
                  ))}
                </select>
              </div>
              <button
                type="button"
                className="btn btn-success"
                onClick={() => handleAddTask(projects[0]._id)} // Replace with the correct project ID
              >
                Add Task
              </button>
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={() => setShowTaskForm(false)}
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}

      <button onClick={() => navigate(-1)} className="btn btn-outline-secondary mt-4">
        Back to Projects
      </button>
    </div>
  );
};

export default TasksPage;
