import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from 'react';
import { Button, Form, Modal } from 'react-bootstrap'; // Import modal components from react-bootstrap
import { useParams } from 'react-router-dom';
import { tasks, users } from '../DummyData'; // Import tasks and users
import './TaskDetails.css'; // Import custom CSS

function TaskDetails() {
  const { taskId } = useParams();
  const [task, setTask] = useState(null);
  const [taskMembers, setTaskMembers] = useState([]);
  const [showEditTaskModal, setShowEditTaskModal] = useState(false);
  const [showAddMembersModal, setShowAddMembersModal] = useState(false);
  const [showConfirmDeleteModal, setShowConfirmDeleteModal] = useState(false); // To confirm delete
  const [memberToRemove, setMemberToRemove] = useState(null); // Store member to remove
  const [editedTask, setEditedTask] = useState({ name: '', description: '', due_date: '', status: '' });
  const [newMemberId, setNewMemberId] = useState(''); // To store the new member selected

  useEffect(() => {
    const selectedTask = tasks.find((t) => t._id === taskId);

    if (selectedTask) {
      setTask(selectedTask);
      setEditedTask(selectedTask);

      const members = selectedTask.assignedTo.map((userId) => {
        const user = users.find((u) => u._id === userId);
        return user ? { ...user } : null;
      }).filter(Boolean);

      setTaskMembers(members);
    } else {
      setTask(null);
    }
  }, [taskId]);

  const handleTaskChange = (e) => {
    setEditedTask({ ...editedTask, [e.target.name]: e.target.value });
  };

  const handleTaskSave = () => {
    setTask({ ...editedTask });
    setShowEditTaskModal(false);
  };

  const handleAddMember = () => {
    const newMember = users.find((u) => u._id === newMemberId);
    if (newMember) {
      setTaskMembers([...taskMembers, newMember]);
    }
    setNewMemberId('');
  };

  const handleRemoveMember = (memberId) => {
    setMemberToRemove(memberId);
    setShowConfirmDeleteModal(true); // Show confirm modal
  };

  const confirmRemoveMember = () => {
    const updatedMembers = taskMembers.filter((m) => m._id !== memberToRemove);
    setTaskMembers(updatedMembers);
    setShowConfirmDeleteModal(false); // Close modal
  };

  if (!task) {
    return <div>Loading task details...</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4" id="heading1">Task Details</h1>

      {/* Task Overview Section */}
      <div className="card shadow-sm mb-4">
        <div className="card-body d-flex justify-content-between">
          <div>
            <h2 className="card-title">{task.name}</h2>
            <p className="card-text">{task.description}</p>
            <ul className="list-unstyled">
              <li><strong>Due Date:</strong> {new Date(task.due_date).toLocaleDateString()}</li>
              <li>
                <strong>Status:</strong>{' '}
                <span className={`badge ${task.status === 'In Progress' || task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                  {task.status}
                </span>
              </li>
            </ul>
          </div>
          <div>
            <Button variant="primary" onClick={() => setShowEditTaskModal(true)}>
              Edit Task
            </Button>
          </div>
        </div>
      </div>

      {/* Task Members Section */}
      <div className="card shadow-sm">
        <div className="card-header d-flex justify-content-between">
          <h3>Assigned Members</h3>
          <Button variant="primary" onClick={() => setShowAddMembersModal(true)}>
            Add Members
          </Button>
        </div>
        <ul className="list-group list-group-flush">
          {taskMembers.map((member) => (
            <li className="list-group-item d-flex justify-content-between align-items-center" key={member._id}>
              <div>
                {member.name} - <span className="text-muted">{member.email}</span>
              </div>
              <Button variant="danger" onClick={() => handleRemoveMember(member._id)}>
                Remove
              </Button>
            </li>
          ))}
        </ul>
        {/* <div className="card-footer">
          <Button variant="success" onClick={() => setShowAddMembersModal(true)}>Add Member</Button>
        </div> */}
      </div>

      {/* Edit Task Modal */}
      <Modal show={showEditTaskModal} onHide={() => setShowEditTaskModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Task Name</Form.Label>
              <Form.Control type="text" name="name" value={editedTask.name} onChange={handleTaskChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" name="description" value={editedTask.description} onChange={handleTaskChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Due Date</Form.Label>
              <Form.Control type="date" name="due_date" value={editedTask.due_date} onChange={handleTaskChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Status</Form.Label>
              <Form.Select name="status" value={editedTask.status} onChange={handleTaskChange}>
                <option value="Ongoing">Ongoing</option>
                <option value="Completed">Completed</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditTaskModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleTaskSave}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Confirm Delete Member Modal */}
      <Modal show={showConfirmDeleteModal} onHide={() => setShowConfirmDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Remove Member</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to remove this member from the task?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowConfirmDeleteModal(false)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={confirmRemoveMember}>
            Confirm Remove
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Edit Members Modal */}
      <Modal show={showAddMembersModal} onHide={() => setShowAddMembersModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Members</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group className="mb-3">
            <Form.Label>Add New Member</Form.Label>
            <Form.Select value={newMemberId} onChange={(e) => setNewMemberId(e.target.value)}>
              <option value="">Select a member</option>
              {users.map((user) => (
                <option key={user._id} value={user._id}>
                  {user.name} ({user.email})
                </option>
              ))}
            </Form.Select>
            <Button variant="success" className="mt-3" onClick={handleAddMember}>
              Add Member
            </Button>
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddMembersModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default TaskDetails;
