import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { useNavigate } from 'react-router-dom';
import { projects } from '../DummyData'; // Assuming the projects array is imported here
import './Dashboard.css';

function Dashboard() {
  const [metrics, setMetrics] = useState({
    totalProjects: 0,
    completedProjects: 0,
    activeProjects: 0,
  });
  const [recentProjects, setRecentProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProjects, setSelectedProjects] = useState([]); // List of projects to display in the modal
  const [showCalendar, setShowCalendar] = useState(false);
  const [modalTitle, setModalTitle] = useState(''); // Title for the modal
  const [ongoingProjectsEndDates, setOngoingProjectsEndDates] = useState([]); // Store end dates of ongoing projects
  const navigate = useNavigate();

  const handleCreateProject = () => {
    navigate('/create-project');
  };

  const handleCardClick = (projectsList, title) => {
    setSelectedProjects(projectsList); // Set the list of projects to be displayed in the modal
    setModalTitle(title);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedProjects([]);
    setModalTitle('');
  };

  const handleViewCalendar = () => {
    setShowCalendar(true);
  };

  const closeCalendar = () => {
    setShowCalendar(false);
  };

  useEffect(() => {
    // Calculate metrics
    const totalProjects = projects.length;
    const completedProjects = projects.filter(
      (project) => project.status === 'Completed'
    ).length;
    const activeProjects = projects.filter(
      (project) => project.status === 'Ongoing'
    ).length;

    setMetrics({
      totalProjects,
      completedProjects,
      activeProjects,
    });

    // Get recent active projects
    const recentActiveProjects = projects.filter(
      (project) => project.status === 'Ongoing'
    );
    setRecentProjects(recentActiveProjects);

    // Collect end dates of ongoing projects
    const ongoingEndDates = recentActiveProjects.map((project) =>
      new Date(project.endDate)
    );
    setOngoingProjectsEndDates(ongoingEndDates);
  }, []);

  // Tile content to highlight ongoing projects' end dates on the calendar
  const tileClassName = ({ date, view }) => {
    if (view === 'month') {
      // Check if the date is one of the ongoing project's end dates
      if (
        ongoingProjectsEndDates.some(
          (endDate) =>
            endDate.getFullYear() === date.getFullYear() &&
            endDate.getMonth() === date.getMonth() &&
            endDate.getDate() === date.getDate()
        )
      ) {
        return 'highlight-tile'; // Add a CSS class to highlight the date
      }
    }
    return null;
  };

  return (
    <div className="container mt-5 dashboard-container">
      <h1 className="text-center mb-4" id="heading1">
        Dashboard
      </h1>

      {/* Metrics Section */}
      <div className="row mb-4">
        <div className="col-md-4">
          <div
            className="card text-center shadow-sm"
            onClick={() => handleCardClick(projects, 'Total Projects')}
          >
            <div className="card-body">
              <h5 className="card-title">Total Projects</h5>
              <p className="card-text display-4">{metrics.totalProjects}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div
            className="card text-center shadow-sm"
            onClick={() =>
              handleCardClick(
                projects.filter((project) => project.status === 'Ongoing'),
                'Active Projects'
              )
            }
          >
            <div className="card-body">
              <h5 className="card-title">Active Projects</h5>
              <p className="card-text display-4">{metrics.activeProjects}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div
            className="card text-center shadow-sm"
            onClick={() =>
              handleCardClick(
                projects.filter((project) => project.status === 'Completed'),
                'Completed Projects'
              )
            }
          >
            <div className="card-body">
              <h5 className="card-title">Completed Projects</h5>
              <p className="card-text display-4">{metrics.completedProjects}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Projects Section */}
      <div className="card shadow-sm mb-4">
        <div className="card-header">
          <h2>Recent Projects</h2>
        </div>
        <ul className="list-group list-group-flush">
          {recentProjects.map((project) => (
            <li 
            className="list-group-item" 
            key={project._id}
            onClick={() => navigate(`/projects/${project._id}`)}
            style={{ cursor: 'pointer' }}  // Corrected 'curser' to 'cursor'
          >
            {project.name}
          </li>
          ))}
        </ul>
      </div>

      {/* Quick Actions Section */}
      <div className="quick-actions-section text-center mt-4">
        <button className="btn btn-primary m-2" onClick={handleCreateProject}>
          Create New Project
        </button>
        <button className="btn btn-secondary m-2" onClick={handleViewCalendar}>
          View Calendar
        </button>
      </div>

      {/* Modal for Project Details */}
{showModal && (
  <div className="modal-backdrop" onClick={closeModal}>
    <div
      className="modal fade show"
      style={{ display: 'block' }}
      tabIndex="-1"
      role="dialog"
    >
      <div className="modal-dialog" role="document">
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <div className="modal-header">
            <h5 className="modal-title">
              {modalTitle}: {selectedProjects.length}
            </h5>
          </div>

          <div className="modal-body">
            <ul className="project-list">
              {selectedProjects.map((project) => (
                <li key={project._id} className="project-item">
                  {project.name}
                </li>
              ))}
            </ul>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={closeModal}>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
)}

      

      {/* Calendar Modal */}
      {showCalendar && (
        <div className="calendar-backdrop">
          <div className="calendar-card card shadow-sm">
            <div className="card-header">
              <h5>Calendar</h5>
              <button
                type="button"
                className="close"
                onClick={closeCalendar}
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="card-body">
              <Calendar tileClassName={tileClassName} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
