import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap styles
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import axios from 'axios'; // Import axios for making HTTP requests
import './TasksPage.css'; // Custom styles
import { useAuth } from "../AuthContext";

const TasksPage = () => {
  const navigate = useNavigate(); // To navigate programmatically
  const [projects, setProjects] = useState([]); // State for projects
  const [newTask, setNewTask] = useState({ name: '', description: '', status: 'Ongoing', due_date: '', assignedTo: [] });
  const [teamMembers, setTeamMembers] = useState([]); // Store team members for the assigned field
  const { user } = useAuth(); // Auth context to get the current user
  const [showTaskForm, setShowTaskForm] = useState({}); // Control task form visibility for each project

  // Fetch projects and tasks when the component mounts
  useEffect(() => {
    const fetchProjectsAndTasks = async () => {
      try {
        if (!user) {
          console.log("User is not logged in.");
          return; // Exit if user is not logged in
        }

        // Fetch projects of the user
        const response = await axios.get(`http://localhost:5000/api/user/${user.id}`);
        const fetchedProjects = response.data.projects; // Access the actual project data
        setProjects(fetchedProjects); // Set the projects state

        // Optionally fetch tasks for all projects if needed
        await Promise.all(fetchedProjects.map(async (project) => {
          const tasksResponse = await axios.get(`http://localhost:5000/api/project/${project._id}/tasks`);
          project.tasks = tasksResponse.data; // Assign fetched tasks to project
        }));

      } catch (error) {
        console.error("Error fetching projects or tasks:", error);
      }
    };

    fetchProjectsAndTasks();
  }, [user]);

  // Handle adding a new task
  const handleAddTask = async (projectId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/task/${projectId}`, newTask);

      // After adding the task, refresh the project list to include the new task
      setProjects((prevProjects) => prevProjects.map((project) =>
        project._id === projectId ? { ...project, tasks: [...project.tasks, response.data] } : project
      ));

      setShowTaskForm((prev) => ({ ...prev, [projectId]: false })); // Hide the task form after adding the task
      setNewTask({ name: '', description: '', status: 'Ongoing', due_date: '', assignedTo: [] }); // Reset the task form
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  // Function to remove a task
  const handleRemoveTask = async (taskId, projectId) => {
    try {
      await axios.delete(`http://localhost:5000/api/task/${taskId}`);
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId
            ? { ...project, tasks: project.tasks.filter((task) => task._id !== taskId) }
            : project
        )
      );
    } catch (error) {
      console.error("Error removing task:", error);
    }
  };

  // Fetch team members for a project
  const fetchTeamMembers = async (projectId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/project/${projectId}`);
      setTeamMembers(response.data.teamMembers); // Set the list of team members
    } catch (error) {
      console.error("Error fetching team members:", error);
    }
  };

  // Handle the form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  const handleAssignedToChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
    setNewTask(prevTask => ({
        ...prevTask,
        assignedTo: selectedOptions  // Set the array of selected user IDs
    }));
  };

  return (
    <div className="container tasks-page mt-4">
      {projects.length > 0 ? (
        projects.map((project) => (
          <div key={project._id} className="card project-detail-card mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h2 className="mb-0">{project.name}
                <span className={`badge ${project.status === 'Ongoing' ? 'bg-warning' : 'bg-success'} ms-2`}>
                  {project.status}
                </span>
              </h2>
              {project.created_by === user.id && (
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    setShowTaskForm((prev) => ({ ...prev, [project._id]: true }));
                    fetchTeamMembers(project._id); // Fetch team members when opening task form
                  }}
                >
                  Add Task
                </button>
              )}
            </div>

            {/* List of User's Tasks for the current project */}
            <div className="card-body">
              <div className="row">
                {project.tasks && project.tasks.length > 0 ? (
                  project.tasks
                    .filter(task => task.assignedTo.includes(user.id)) // Filter tasks assigned to the logged-in user
                    .map((task) => (
                      <div key={task._id} className="col-12 mb-3">
                        <div className="card h-100 task-card">
                          <div className="card-body">
                            <h5 className="card-title">{task.name}</h5>
                            <p className="card-text">Description: {task.description}</p>
                            <p className="card-text">
                              <span className={`badge ${task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                                {task.status}
                              </span>
                            </p>
                            <p className="card-text">Due Date: {new Date(task.due_date).toLocaleDateString()}</p>
                            <div className="d-flex justify-content-between">
                              <button
                                className="btn btn-outline-primary"
                                onClick={() => navigate(`/task-details/${task._id}`)}
                              >
                                Manage
                              </button>
                              {project.created_by === user.id && (
                                <button
                                  className="btn btn-danger btn-sm"
                                  onClick={() => handleRemoveTask(task._id, project._id)}
                                >
                                  Remove
                                </button>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    ))
                ) : (
                  <p>No tasks found for this project.</p>
                )}
              </div>

              {/* Add Task Form specific to each project */}
              {showTaskForm[project._id] && (
                <div className="card task-form-card mt-4">
                  <div className="card-body">
                    <h5>Add New Task</h5>
                    <form onSubmit={(e) => { e.preventDefault(); handleAddTask(project._id); }}>
                      <div className="mb-3">
                        <label htmlFor="taskName" className="form-label">Task Name</label>
                        <input type="text" className="form-control" id="taskName" name="name" value={newTask.name} onChange={handleInputChange} required />
                      </div>
                      <div className="mb-3">
                        <label htmlFor="taskDescription" className="form-label">Description</label>
                        <textarea className="form-control" id="taskDescription" name="description" value={newTask.description} onChange={handleInputChange} required></textarea>
                      </div>
                      <div className="mb-3">
                        <label htmlFor="taskStatus" className="form-label">Status</label>
                        <select className="form-select" id="taskStatus" name="status" value={newTask.status} onChange={handleInputChange}>
                          <option value="Ongoing">Ongoing</option>
                          <option value="Completed">Completed</option>
                          <option value="Pending">Pending</option>
                        </select>
                      </div>
                      <div className="mb-3">
                        <label htmlFor="taskDueDate" className="form-label">Due Date</label>
                        <input type="date" className="form-control" id="taskDueDate" name="due_date" value={newTask.due_date} onChange={handleInputChange} required />
                      </div>
                      <div className="mb-3">
                        <label htmlFor="assignedTo" className="form-label">Assign To</label>
                        <select multiple className="form-select" id="assignedTo" name="assignedTo" value={newTask.assignedTo} onChange={handleAssignedToChange}>
                          {teamMembers.map(member => (
                            <option key={member.id} value={member.id}>{member.name}</option>
                          ))}
                        </select>
                      </div>
                      <button type="submit" className="btn btn-success">Add Task</button>
                      <button type="button" className="btn btn-secondary ms-2" onClick={() => setShowTaskForm((prev) => ({ ...prev, [project._id]: false }))}>Cancel</button>
                    </form>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))
      ) : (
        <h4>No projects available.</h4>
      )}
    </div>
  );
