import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap styles
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom'; // For extracting projectId and navigation
import { projects, tasks } from '../DummyData'; // Import dummy data
import './TasksPage.css'; // Custom styles

const TasksPage = () => {
  const { projectId } = useParams(); // Extract projectId from URL
  const navigate = useNavigate(); // To navigate programmatically
  const [showTaskForm, setShowTaskForm] = useState(false); // State to control task form visibility
  const [projectDetails, setProjectDetails] = useState(null); // State for project details
  const [projectTasks, setProjectTasks] = useState([]); // State for tasks of the current project
  const [newTask, setNewTask] = useState({ name: '', description: '', status: 'Ongoing', due_date: '' });

  // Fetch project details and tasks when the component mounts or projectId changes
  useEffect(() => {
    const project = projects.find(proj => proj._id === projectId);
    if (project) {
      setProjectDetails(project);
      const tasksForProject = tasks.filter(task => task.project_id === projectId);
      setProjectTasks(tasksForProject);
    }
  }, [projectId]);

  // Handle form submission to add a new task
  const handleAddTask = () => {
    const newTaskWithId = { ...newTask, _id: Date.now().toString(), project_id: projectId }; // Create a new task with a unique ID
    setProjectTasks([...projectTasks, newTaskWithId]); // Add the new task to the current project's tasks
    setShowTaskForm(false); // Hide form after submission
    setNewTask({ name: '', description: '', status: 'Ongoing', due_date: '' }); // Reset new task state
  };

  // Handle removing a task with confirmation
  const handleRemoveTask = (taskId) => {
    const isConfirmed = window.confirm("Are you sure you want to remove this task?");
    if (isConfirmed) {
      setProjectTasks(projectTasks.filter(task => task._id !== taskId)); // Filter out the removed task
    }
  };


  // Render project details and tasks if projectId exists
  if (projectId && projectDetails) {
    return (
      <div className="container tasks-page mt-4">
        <div className="card project-detail-card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h2 className="mb-0">{projectDetails.name}
              <span className={`badge ${projectDetails.status === 'Ongoing' ? 'bg-warning' : 'bg-success'} ms-2`}>
                {projectDetails.status}
              </span>
            </h2>
            {/* Add Task Button */}
            <button
              className="btn btn-primary"
              onClick={() => setShowTaskForm(true)} // Show form on click
            >
              Add Task
            </button>
          </div>

          {/* Task Form */}
          {showTaskForm && (
            <div className="backdrop">
              <div className="task-form p-4 bg-white rounded shadow">
                <h3>Add New Task</h3>
                <div className="mb-3">
                  <label>Task Name:</label>
                  <input
                    type="text"
                    className="form-control"
                    value={newTask.name}
                    onChange={(e) => setNewTask({ ...newTask, name: e.target.value })}
                  />
                </div>
                <div className="mb-3">
                  <label>Description:</label>
                  <textarea
                    className="form-control"
                    value={newTask.description}
                    onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                  />
                </div>
                <div className="mb-3">
                  <label>Due Date:</label>
                  <input
                    type="date"
                    className="form-control"
                    value={newTask.due_date}
                    onChange={(e) => setNewTask({ ...newTask, due_date: e.target.value })}
                  />
                </div>
                <div className="mb-3">
                  <label>Status:</label>
                  <select
                    className="form-select"
                    value={newTask.status}
                    onChange={(e) => setNewTask({ ...newTask, status: e.target.value })}
                  >
                    <option value="Ongoing">Ongoing</option>
                    <option value="Completed">Completed</option>
                  </select>
                </div>
                <div className="d-flex justify-content-end">
                  <button className="btn btn-success me-2" onClick={handleAddTask}>Add</button>
                  <button className="btn btn-secondary" onClick={() => setShowTaskForm(false)}>Cancel</button>
                </div>
              </div>
            </div>
          )}

          {/* List of Tasks */}
          <div className="card-body">
            <div className="row">
              {projectTasks.map((task) => (
                <div key={task._id} className="col-12 mb-3"> {/* Adjust to col-12 for full row */}
                  <div className="card h-100 task-card"> {/* Add mb-3 for spacing between tasks */}
                    <div className="card-body">
                      <h5 className="card-title">{task.name}</h5>
                      <p className="card-text">Description: {task.description}</p>
                      <p className="card-text">
                        <span className={`badge ${task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                          {task.status}
                        </span>
                      </p>
                      <p className="card-text">Due Date: {new Date(task.due_date).toLocaleDateString()}</p>
                      <div className="d-flex justify-content-between">
                        <button
                          className="btn btn-outline-primary"
                          onClick={() => navigate(`/task-details/${task._id}`)}
                        >
                          Manage
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleRemoveTask(task._id)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

         </div>
        <button onClick={() => navigate(-1)} className="btn btn-outline-secondary mt-4">Back to Projects</button>
      </div>
    );
  }

  // If no projectId in URL or invalid projectId, show the default projects list with tasks
  return (
    <div className="container tasks-page mt-4">
      <div className="row">
        {projects.map((project) => {
          const projectTasks = tasks.filter(task => task.project_id === project._id); // Filter tasks for the project
          return (
            <div key={project._id} className="col-md-12 mb-3"> {/* Adjust col-md-12 to have only one project per row */}
              <div className="card project-card">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">{project.name}</h5>
                  <button
                    className="btn btn-outline-primary"
                    onClick={() => navigate(`/taskspage/${project._id}`)}
                  >
                    Manage
                  </button>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">
                    Status: <span className={`badge ${project.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>{project.status}</span>
                  </li>
                  <li className="list-group-item project-task-list">
                    <strong>Tasks:</strong>
                    <ul>
                      {projectTasks.length > 0 ? (
                        projectTasks.map(task => (
                          <li key={task._id}
                            onClick={() => navigate(`/task-details/${task._id}`)} // Navigate when clicking the li element
                            style={{ cursor: 'pointer' }} // Add pointer cursor for better UX              
                          >
                            {task.name}
                          </li>
                        ))
                      ) : (
                        <li>No tasks assigned</li>
                      )}
                    </ul>
                  </li>
                </ul>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );


};

export default TasksPage;
