import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './ProjectDetails.css'; // Import custom CSS

function ProjectDetails() {
  const { projectId } = useParams(); // Get projectId from URL
  const [project, setProject] = useState(null);
  const navigate = useNavigate();
  const [isProjectTask,setisProjectTask]=useState(false);
  const [projectTasks, setProjectTasks] = useState([]);
  const [isTeamMembers,setisTeamMembers]=useState(false);
  const [teamMembers, setTeamMembers] = useState([]);
  const [isEditing, setIsEditing] = useState(false); // State for handling edit mode
  const [editedProject, setEditedProject] = useState({});

  useEffect(() => {
    const fetchProjectDetails = async () => {
      try {
        // Fetch project details, tasks, and team members
        const response = await axios.get(`http://localhost:5000/api/project/${projectId}`);
        const { project, tasks } = response.data;
        
        console.log(project);
        console.log(tasks);
        console.log(teamMembers);

        if(tasks){
          setProjectTasks(tasks);
          setisProjectTask(true);
        }
        setProject(project);
        if(teamMembers){
          setTeamMembers(teamMembers);
          setisTeamMembers(true);
        }
        
        setEditedProject(project);
      } catch (err) {
        console.error('Error fetching project details:', err);
      }
    };

    fetchProjectDetails();
  }, [projectId]);

  // Handle input changes for the editable form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProject({ ...editedProject, [name]: value });
  };

  // Function to save the updated project details
  const handleSave = () => {
    setProject(editedProject);
    setIsEditing(false);
    document.body.classList.remove('body-no-scroll');
  };

  // Handle opening of the modal
  const openEditModal = () => {
    setIsEditing(true);
    document.body.classList.add('body-no-scroll'); // Disable scrolling
  };

  // Handle closing of the modal
  const closeEditModal = () => {
    setIsEditing(false);
    document.body.classList.remove('body-no-scroll'); // Re-enable scrolling
  };

  if (!project) {
    return <div>Loading project details...</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4" id="heading1">Project Details</h1>

      {/* Project Overview Section */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="d-flex justify-content-between">
            <h2 className="card-title">{project?.name}</h2>
            <button className="btn btn-primary" onClick={openEditModal}>
              Edit
            </button>
          </div>
          <p className="card-text">{project?.description}</p>
          <h4 className="card-title">
            <pre style={{ fontFamily: 'inherit' }}>
              Start Date: {new Date(project.startDate).toLocaleDateString()}          <span style={{ color: 'red' }}>Due Date:</span> {new Date(project.endDate).toLocaleDateString()}
            </pre>
          </h4>
          
          <span className={`badge ${project?.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
            {project?.status}
          </span>
        </div>
      </div>

      {/* Tasks Section */}
      <div className="card shadow-sm mb-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h3>Tasks</h3>
          <button
            className="btn btn-outline-primary"
            onClick={() => navigate(`/taskspage/${project._id}`)}
          >
            Manage
          </button>
        </div>

{isProjectTask &&      (
    <ul className="list-group list-group-flush">
          {projectTasks.map((task) => (
            <li className="list-group-item d-flex justify-content-between align-items-center" key={task._id}
            onClick={() => navigate(`/task-details/${task._id}`)} // Navigate when clicking the li element
            style={{ cursor: 'pointer' }} // Add pointer cursor for better UX  
            >
                {task.name}
              <span className={`badge ${task.status === 'Completed' ? 'bg-success' : task.status === 'Ongoing' ? 'bg-warning' : 'bg-secondary'}`}>
                {task.status}
              </span>
            </li>
          ))}
        </ul>
)}
      </div>

      {/* Team Members Section */}
      <div className="card shadow-sm">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h3>Team Members</h3>
          <button 
            className="btn btn-outline-primary" 
            onClick={() => window.location.href = `/team-management/${projectId}`}
          >
            Manage
          </button>
        </div>
        {isTeamMembers && ( <ul className="list-group list-group-flush">
          {teamMembers.map((member) => (
            <li className="list-group-item" key={member._id}>
              {member.user_id.name} - <span className="text-muted">{member.role}</span>
            </li>
          ))}
        </ul>
        )}
      </div>

      {/* Editable Form Modal (Backdrop) */}
      {isEditing && (
        <div className="backdrop">
          <div className="modal-content p-4">
            <h3>Edit Project Details</h3>
            <div className="form-group">
              <label>Project Name</label>
              <input
                type="text"
                className="form-control"
                name="name"
                value={editedProject.name}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                className="form-control"
                name="description"
                value={editedProject.description}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Start Date</label>
              <input
                type="date"
                className="form-control"
                name="startDate"
                value={new Date(editedProject.startDate).toISOString().substr(0, 10)}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>End Date</label>
              <input
                type="date"
                className="form-control"
                name="endDate"
                value={new Date(editedProject.endDate).toISOString().substr(0, 10)}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Status</label>
              <select
                className="form-control"
                name="status"
                value={editedProject.status}
                onChange={handleInputChange}
              >
                <option value="Ongoing">Ongoing</option>
                <option value="Completed">Completed</option>
              </select>
            </div>
            <div className="d-flex justify-content-end">
              <button className="btn btn-secondary ms-3" onClick={closeEditModal}>
                Cancel
              </button>
              <button className="btn btn-primary ms-3" onClick={handleSave}>
                Save
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ProjectDetails;
