import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap styles
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom'; // For extracting projectId and navigation
import axios from 'axios'; // Import axios for making HTTP requests
import './TasksPage.css'; // Custom styles
import { useAuth } from "../AuthContext";

const TasksPage = () => { // Accept userId as a prop
  // Extract projectId from URL
  const navigate = useNavigate(); // To navigate programmatically
  const [showTaskForm, setShowTaskForm] = useState(false); // State to control task form visibility
  const [projects, setProjects] = useState([]); // State for projects where user is a team member
  const [projectDetails, setProjectDetails] = useState(null); // State for project details
  const [userTasks, setUserTasks] = useState([]); // State for tasks of the current project
  const [newTask, setNewTask] = useState({ name: '', description: '', status: 'Ongoing', due_date: '' });
  const { user } = useAuth();
  // const navigate = useNavigate();

  const userId=user.id;
  // Fetch projects and tasks when the component mounts
  useEffect(() => {


  // Fetching the projects data
  const fetchProjectsAndTasks = async () => {
    try {
      if (!user) {
        console.log("User is not logged in.");
        return; // Exit if user is not logged in
      }
      console.log(user);
      const response = await axios.get(`http://localhost:5000/api/user/${user.id}/projects`);
      const projects = response.data; // Access the actual project data
      console.log(projects);

        const tasksResponse = await axios.get(`http://localhost:5000/api/user/${user.id}/tasks`);
        console.log(tasksResponse.data);
        setUserTasks(tasksResponse.data);

       // Fetch tasks for the specific project


        // If a projectId is provided in the URL, fetch its details and tasks

      } catch (error) {
        console.error("Error fetching projects or tasks:", error);
      }
    };

    fetchProjectsAndTasks();
  }, [ userId]); // Added userId to dependency array

  //Handle form submission to add a new task

  const handleAddTask = async () => {
    // try {
    //   // const response = await axios.post(`/api/tasks`, { ...newTask, project_id: projectId, user_id: userId }); // Include user_id when adding a task
    //   setProjectTasks([...projectTasks, response.data]); // Add the new task to the current project's tasks
    //   setShowTaskForm(false); // Hide form after submission
    //   setNewTask({ name: '', description: '', status: 'Ongoing', due_date: '' }); // Reset new task state
    // } catch (error) {
    //   console.error("Error adding task:", error);
    // }
  };


  // Handle removing a task with confirmation
  const handleRemoveTask = async (taskId) => {
    // const isConfirmed = window.confirm("Are you sure you want to remove this task?");
    // if (isConfirmed) {
    //   try {
    //     await axios.delete(`/api/tasks/${taskId}`); // Send request to delete task
    //     setProjectTasks(projectTasks.filter(task => task._id !== taskId)); // Filter out the removed task
    //   } catch (error) {
    //     console.error("Error removing task:", error);
    //   }
    // }
  };

  // Render only projects where the user is a team member
  return (
    <div className="container tasks-page mt-4">
      {/* {projects.length > 0 ? (
        projects.map((project) => (
          <div key={project._id} className="card project-detail-card mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h2 className="mb-0">{project.name}
                <span className={`badge ${project.status === 'Ongoing' ? 'bg-warning' : 'bg-success'} ms-2`}>
                  {project.status}
                </span>
              </h2>
              <button
                className="btn btn-primary"
                onClick={() => {
                  setShowTaskForm(true);
                  setProjectDetails(project); // Set the current project details
                  setUserTasks([]); // Clear previous tasks
                }} // Show form for the selected project
              >
                Add Task
              </button>
            </div> */}

            {/* Task Form */}
            {showTaskForm && (
              <div className="backdrop">
                <div className="task-form p-4 bg-white rounded shadow">
                  <h3>Add New Task</h3>
                  <div className="mb-3">
                    <label>Task Name:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={newTask.name}
                      onChange={(e) => setNewTask({ ...newTask, name: e.target.value })}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Description:</label>
                    <textarea
                      className="form-control"
                      value={newTask.description}
                      onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Due Date:</label>
                    <input
                      type="date"
                      className="form-control"
                      value={newTask.due_date}
                      onChange={(e) => setNewTask({ ...newTask, due_date: e.target.value })}
                    />
                  </div>
                  <div className="mb-3">
                    <label>Status:</label>
                    <select
                      className="form-select"
                      value={newTask.status}
                      onChange={(e) => setNewTask({ ...newTask, status: e.target.value })}
                    >
                      <option value="Ongoing">Ongoing</option>
                      <option value="Completed">Completed</option>
                    </select>
                  </div>
                  <div className="d-flex justify-content-end">
                    <button className="btn btn-success me-2" onClick={handleAddTask}>Add</button>
                    <button className="btn btn-secondary" onClick={() => setShowTaskForm(false)}>Cancel</button>
                  </div>
                </div>
              </div>
            )}

            {/* List of Tasks */}
            <div className="card-body">
              <div className="row">
                {userTasks.map((task) => (
                  <div key={task._id} className="col-12 mb-3"> {/* Adjust to col-12 for full row */}
                    <div className="card h-100 task-card"> {/* Add mb-3 for spacing between tasks */}
                      <div className="card-body">
                        <h5 className="card-title">{task.name}</h5>
                        <p className="card-text">Description: {task.description}</p>
                        <p className="card-text">
                          <span className={`badge ${task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                            {task.status}
                          </span>
                        </p>
                        <p className="card-text">Due Date: {new Date(task.due_date).toLocaleDateString()}</p>
                        <div className="d-flex justify-content-between">
                          <button
                            className="btn btn-outline-primary"
                            onClick={() => navigate(`/task-details/${task._id}`)}
                          >
                            Manage
                          </button>
                          <button
                            className="btn btn-danger btn-sm"
                            onClick={() => handleRemoveTask(task._id)}
                          >
                            Remove
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )
      //  : (
      //   <p>No tasks found for the current user.</p>
      // )}
}
      <button onClick={() => navigate(-1)} className="btn btn-outline-secondary mt-4">Back to Projects</button>
    // </div>
  // );
// };

export default TasksPage;
