import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import './CreateProject.css'; // Import custom CSS
import axios from 'axios';

function CreateProject() {
  const [projectName, setProjectName] = useState('');
  const [endDate, setEndDate] = useState('');
  const [startDate, setStartDate] = useState('');
  const [description, setDescription] = useState('');

  const navigate=useNavigate();
  const handleCancelProject = () => {
    navigate('/Dashboard'); // Redirect to dashboard page
  };

  const handleSubmit = async(e) => {
    e.preventDefault();
    // Handle project creation logic here

    const response = await fetch('http://localhost:5000/api/project', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ projectName, startDate, endDate,description }), // Send data to backend
    })// Adjust the endpoint as necessary
      const projects = response.data.projects;
    if(!response.ok){
      console.log("error");
    }
    else{
    console.log('Project Created:', projects);
    // Optionally reset the form
    setProjectName('');
    setEndDate('');
    setStartDate('');
    setDescription('');
    navigate('/Dashboard'); 
    }// Redirect to Dashboard page
  };

  return (
    <div className="container mt-5 newProject-container">
      <h2 className="text-center mb-4">Create New Project</h2>
      <form onSubmit={handleSubmit} className="shadow p-4 rounded bg-white">
        <div className="mb-3">
          <label htmlFor="projectName" className="form-label">Project Name</label>
          <input 
            type="text" 
            className="form-control" 
            id="projectName" 
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)} 
            required 
          />
        </div>
        <div className="mb-3">
          <label htmlFor="startDate" className="form-label">Start Date</label>
          <input 
            type="date" 
            className="form-control" 
            id="startDate" 
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)} 
            required 
          />
        </div>
        <div className="mb-3">
          <label htmlFor="endDate" className="form-label">End Date</label>
          <input 
            type="date" 
            className="form-control" 
            id="endDate" 
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)} 
            required 
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <textarea 
            className="form-control" 
            id="description" 
            rows="3" 
            value={description}
            onChange={(e) => setDescription(e.target.value)} 
            required 
          />
        </div>
        <div className="text-center">
          <button type="submit" className="btn btn-primary">Create Project</button>
          <button className="btn btn-secondary m-2" onClick={handleCancelProject}>Cancel</button>
        </div>
       
      </form>
    </div>
  );
}

export default CreateProject;
