import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap styles
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import axios from 'axios'; // Import axios for making HTTP requests
import './TasksPage.css'; // Custom styles
import { useAuth } from "../AuthContext";

const TasksPage = () => {
  const navigate = useNavigate(); // To navigate programmatically
  const [projects, setProjects] = useState([]); // State for projects where user is a team member
  const { user } = useAuth(); // Auth context to get the current user
  const [showTaskForm, setShowTaskForm] = useState(null); // Control task form visibility for specific project
  const [newTask, setNewTask] = useState({ name: '', description: '', status: 'Ongoing', due_date: '' });

  // Fetch projects and tasks when the component mounts
  useEffect(() => {
    const fetchProjectsAndTasks = async () => {
      try {
        if (!user) {
          console.log("User is not logged in.");
          return; // Exit if user is not logged in
        }

        // Fetch projects of the user
        const response = await axios.get(`http://localhost:5000/api/user/${user.id}/projects`);
        const fetchedProjects = response.data; // Access the actual project data
        console.log(response.data);

        setProjects(fetchedProjects); // Set the projects state
      } catch (error) {
        console.error("Error fetching projects or tasks:", error);
      }
    };

    fetchProjectsAndTasks();
  }, [user]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  // Handle adding a new task
  const handleAddTask = async (projectId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api//${projectId}/tasks`, newTask); // POST request to add a new task
      const createdTask = response.data; // The newly created task

      // Update the state to reflect the new task in the project
      setProjects((prevProjects) =>
        prevProjects.map((project) =>
          project._id === projectId
            ? { ...project, tasks: [...project.tasks, createdTask] }
            : project
        )
      );

      // Reset the form and hide it
      setNewTask({ name: '', description: '', status: 'Ongoing', due_date: '' });
      setShowTaskForm(null);
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  // Handle removing a task
  const handleRemoveTask = async (taskId) => {
    const isConfirmed = window.confirm("Are you sure you want to remove this task?");
    if (isConfirmed) {
      try {
        await axios.delete(`/api/tasks/${taskId}`); // Send request to delete task
        setProjects(projects.map((project) => ({
          ...project,
          tasks: project.tasks.filter((task) => task._id !== taskId),
        })));
      } catch (error) {
        console.error("Error removing task:", error);
      }
    }
  };

  return (
    <div className="container tasks-page mt-4">
      {projects.length > 0 ? (
        projects.map((project) => (
          <div key={project._id} className="card project-detail-card mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h2 className="mb-0">{project.name}
                <span className={`badge ${project.status === 'Ongoing' ? 'bg-warning' : 'bg-success'} ms-2`}>
                  {project.status}
                </span>
              </h2>
              {/* Show the Add Task button only if the user is the project manager */}
              {project.created_by === user.id && (
                <button
                  className="btn btn-primary"
                  onClick={() => setShowTaskForm(project._id)} // Show the form for this project
                >
                  Add Task
                </button>
              )}
            </div>

            {/* List of Tasks for the current project */}
            <div className="card-body">
              <div className="row">
                {project.tasks && project.tasks.length > 0 ? (
                  project.tasks.map((task) => (
                    <div key={task._id} className="col-12 mb-3">
                      <div className="card h-100 task-card">
                        <div className="card-body">
                          <h5 className="card-title">{task.name}</h5>
                          <p className="card-text">Description: {task.description}</p>
                          <p className="card-text">
                            <span className={`badge ${task.status === 'Ongoing' ? 'bg-warning' : 'bg-success'}`}>
                              {task.status}
                            </span>
                          </p>
                          <p className="card-text">Due Date: {new Date(task.due_date).toLocaleDateString()}</p>
                          <p className="card-text">Project: {project.name}</p>
                          <div className="d-flex justify-content-between">
                            <button
                              className="btn btn-outline-primary"
                              onClick={() => navigate(`/task-details/${task._id}`)}
                            >
                              Manage
                            </button>
                            <button
                              className="btn btn-danger btn-sm"
                              onClick={() => handleRemoveTask(task._id)}
                            >
                              Remove
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No tasks found for this project.</p>
                )}
              </div>
            </div>

            {/* Add Task Form (conditionally displayed if showTaskForm is true for this project) */}
            {showTaskForm === project._id && (
              <div className="card-body">
                <h5>Add New Task</h5>
                <form onSubmit={(e) => {
                  e.preventDefault();
                  handleAddTask(project._id); // Add the task to the project
                }}>
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Task Name</label>
                    <input
                      type="text"
                      className="form-control"
                      id="name"
                      name="name"
                      value={newTask.name}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <textarea
                      className="form-control"
                      id="description"
                      name="description"
                      value={newTask.description}
                      onChange={handleInputChange}
                      required
                    ></textarea>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="due_date" className="form-label">Due Date</label>
                    <input
                      type="date"
                      className="form-control"
                      id="due_date"
                      name="due_date"
                      value={newTask.due_date}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-success">Add Task</button>
                </form>
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No projects found for the current user.</p>
      )}

      <button onClick={() => navigate(-1)} className="btn btn-outline-secondary mt-4">
        Back to Projects
      </button>
    </div>
  );
};

export default TasksPage;
